import React, { useEffect, useState } from 'react'
import { useHistory, useParams } from 'react-router-dom'
import styled from 'styled-components'
import axios from 'axios'

import { Message } from '../../components/styles'
import Spinner from '../../components/Spinner'

const Container = styled.div`
  padding: 48px;
  display: flex;

  @media (max-width: 768px) {
    flex-direction: column;
    align-items: center;
  }
`
const ShowImage = styled.img``

const Title = styled.div`
  font-size: 32px;
  font-weight: 600;
`

const Summary = styled.div``

const InfoContainer = styled.div`
  margin-left: 16px;

  @media (max-width: 768px) {
    margin-top: 16px;
  }
`

const ShowDetails = () => {
  const history = useHistory()
  const { showId } = useParams()
  const [showInfo, setShowInfo] = useState(null)
  const [isLoading, setIsLoading] = useState(false)
  const [error, setError] = useState(false)

  useEffect(() => {
    const fetchShowById = async id => {
      setIsLoading(true)
      try {
        const response = await axios.get(`https://api.tvmaze.com/shows/${id}`, {
          params: { embed: 'episodes' }
        })
        setShowInfo(response.data)
        setIsLoading(false)
      } catch (e) {
        setIsLoading(false)
        setError(true)
      }
    }

    fetchShowById(showId)
  }, [showId])

  if (isLoading) {
    return <Spinner />
  }

  if (error || !showInfo) {
    return <Message>Something went wrong..</Message>
  }

  return (
    <Container>
      <ShowImage
        src={
          showInfo.image?.medium ||
          'data:image/png;base64,'
        }
      />
      <InfoContainer>
        <Title>{showInfo.name}</Title>
        <Summary dangerouslySetInnerHTML={{ __html: showInfo.summary }} />
      </InfoContainer>
      <div>asdfa</div>
    </Container>
  )
}

export default ShowDetails
